import filterEmoji from "./filterEmoji";

describe("filterEmoji function", () => {
  test("correctly filters emojis based on search text", () => {
    const searchText = "smile";
    const maxResults = 10;
    const filteredEmojis = filterEmoji(searchText, maxResults);

    expect(filteredEmojis.length).toBeGreaterThan(0); // Filtrelenmiş emoji sayısının 0'dan büyük olması bekleniyor

    filteredEmojis.forEach(emoji => {
      expect(emoji.title.toLowerCase().includes(searchText.toLowerCase()) || emoji.keywords.includes(searchText)).toBeTruthy();
    });
  });
});
